import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import { useLocation } from 'react-router-dom';
import './ListService.css'; // Ensure this CSS supports your new editable fields

const ListService = () => {
  const [services, setServices] = useState([]);
  const location = useLocation();
  const { user } = location.state || {};
  const [selectedServiceId, setSelectedServiceId] = useState('');
  const [serviceTypesEnabled, setServiceTypesEnabled] = useState(false);

  useEffect(() => {
    const fetchServiceTypes = async () => {
      if (selectedServiceId) {
        try {
          const response = await axios.get(`http://localhost:5000/api/servicetypes/${selectedServiceId}`);
          setAllServiceTypes(response.data);
          setServiceTypesEnabled(true); // Enable the Service_Type dropdown
        } catch (error) {
          console.error('Failed to fetch service types:', error);
        }
      } else {
        setAllServiceTypes([]); // Reset service types if no service is selected
        setServiceTypesEnabled(false); // Keep the Service_Type dropdown disabled
      }
    };
  
    fetchServiceTypes();
  }, [selectedServiceId]); // Depend on selectedServiceId
  

  useEffect(() => {
    const fetchServices = async () => {
      try {
        if (user && user.id) {
          const response = await axios.get(`http://localhost:5000/api/servicecatalog/user/${user.id}`);
          const servicesData = response.data.map(service => ({
            ...service,
            isEditing: false,
          }));
          setServices(servicesData);
        }
      } catch (error) {
        console.error('Failed to fetch services:', error);
      }
    };

    fetchServices();
  }, [user.id]);

  const handleEdit = (serviceCatalogId) => {
    const updatedServices = services.map(service => {
      if (service.ServiceCatalog_Id === serviceCatalogId) {
        return { ...service, isEditing: !service.isEditing };
      }
      return service;
    });
    setServices(updatedServices);
  };

  const handleChange = (serviceCatalogId, field, value) => {
    const updatedServices = services.map(service => {
      if (service.ServiceCatalog_Id === serviceCatalogId) {
        return { ...service, [field]: value };
      }
      return service;
    });
    setServices(updatedServices);
  };

  const handleSave = async (serviceCatalogId) => {
    const service = services.find(service => service.ServiceCatalog_Id === serviceCatalogId);
    try {
      await axios.put(`http://localhost:5000/api/servicecatalog/${serviceCatalogId}`, {
        Service_Id: service.Service_Id,
        Service_Type_ID: service.Service_Type_ID,
        Price: service.Price,
        Description: service.Description,
        Service_Provider_ID: user.id, // Assuming this is required for authorization checks
      });
      handleEdit(serviceCatalogId); // Toggle editing off
    } catch (error) {
      console.error('Failed to update service:', error);
    }
  };

  const handleServiceSelection = (e) => {
    const serviceId = e.target.value;
    setSelectedServiceId(serviceId);
    // Optionally reset the service type selection here
  };  

  if (!user) {
    return <div><p>User details not available.</p></div>;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="service-list-container">
        <table>
          <thead>
            <tr>
              <th>Service Name</th>
              <th>Service Type</th>
              <th>Price</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {services.map(service => (
              <tr key={service.ServiceCatalog_Id}>
                <td>
                  {service.isEditing ? (
                    <input
                      type="text"
                      value={service.Service_Name}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Service_Name', e.target.value)}
                    />
                  ) : (
                    service.Service_Name
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <input
                      type="text"
                      value={service.Service_Type}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Service_Type', e.target.value)}
                    />
                  ) : (
                    service.Service_Type
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <input
                      type="number"
                      value={service.Price}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Price', e.target.value)}
                    />
                  ) : (
                    service.Price
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <input
                      type="text"
                      value={service.Description}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Description', e.target.value)}
                    />
                  ) : (
                    service.Description
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <button onClick={() => handleSave(service.ServiceCatalog_Id)}>Save</button>
                  ) : (
                    <button onClick={() => handleEdit(service.ServiceCatalog_Id)}>Modify</button>
                  )}
                  <button onClick={() => handleDelete(service.ServiceCatalog_Id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListService;
