import React, { useEffect } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
  const navigate = useNavigate();
  const { serviceId } = useParams();
  const location = useLocation();
  const { user } = location.state || {}; // Ensure `user` is defaulted to an object

  useEffect(() => {
    // Check if user exists, if not, redirect to login
    if (!user || Object.keys(user).length === 0) {
      navigate('/login');
    }
  }, [user, navigate]);

  if (!user || Object.keys(user).length === 0) {
    // Return null or a loader if the user is not defined
    return null;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        {/* Display serviceId and other user details */}
        <p>Service ID: {serviceId}</p>
        <p>User ID: {user.id}</p>
        {/* Add form and other components here */}
      </div>
    </div>
  );
};

export default ModifyService;
