import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
  const { ServiceCatalog_Id } = useParams();
  const location = useLocation();
  const { user } = location.state || {};
  const [serviceDetails, setServiceDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  
  useEffect(() => {
    const fetchServiceDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`);
        if (response.data.success) {
          setServiceDetails(response.data.data);
        } else {
          throw new Error('Service details not found');
        }
      } catch (error) {
        console.error('Error fetching service details:', error);
      }
      setIsLoading(false);
    };

    fetchServiceDetails();
  }, [ServiceCatalog_Id]);

  const handleServiceTypeChange = (e) => {
    setServiceDetails({ ...serviceDetails, Service_Type: e.target.value });
  };

  const handlePriceChange = (e) => {
    setServiceDetails({ ...serviceDetails, Price: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Ensure you have the correct API endpoint and body for the update operation
      const updateResponse = await axios.put(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`, {
        ...serviceDetails
      });

      if (updateResponse.data.success) {
        alert('Service updated successfully!');
      } else {
        throw new Error('Failed to update service');
      }
    } catch (error) {
      console.error('Error updating service:', error);
      alert('Error updating service: ' + error.message);
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Service Name:
            <input type="text" value={serviceDetails?.Service_Name || ''} disabled />
          </label>
          <br/>
          <label>
            Service Type:
            <input 
              type="text" 
              value={serviceDetails?.Service_Type || ''} 
              onChange={handleServiceTypeChange} 
            />
          </label>
          <br/>
          <label>
            Price:
            <input 
              type="number" 
              value={serviceDetails?.Price || ''} 
              onChange={handlePriceChange} 
            />
          </label>
          <br/>
          <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
};

export default ModifyService;
