import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
  const navigate = useNavigate();
  const { ServiceCatalog_Id } = useParams();
  const { user } = useLocation().state || {};
  const [serviceDetails, setServiceDetails] = useState({});
  const [serviceTypes, setServiceTypes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchServiceDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`);
        setServiceDetails(response.data);
        const serviceTypesResponse = await axios.get(`http://localhost:5000/api/servicetypes/${response.data.Service_Id}`);
        setServiceTypes(serviceTypesResponse.data);
      } catch (error) {
        console.error('Error fetching service details:', error);
        navigate('/error'); // Navigate to an error page or handle error
      } finally {
        setIsLoading(false);
      }
    };

    console.log(user.id, {ServiceCatalog_Id});
    if (user && ServiceCatalog_Id) {
      fetchServiceDetails();
    } else {
    //   navigate('/login'); // Redirect to login if user is not defined
    }
  }, [navigate, ServiceCatalog_Id, user]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  const handlePriceChange = (e) => {
    setServiceDetails({ ...serviceDetails, Price: e.target.value });
  };

  const handleServiceTypeChange = (e) => {
    setServiceDetails({ ...serviceDetails, Service_Type_ID: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`, serviceDetails);
      alert('Service updated successfully!');
      navigate('/list-services'); // Redirect back to the service list or another appropriate page
    } catch (error) {
      console.error('Error updating service:', error);
    }
  };

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Service Name:
            <input type="text" value={serviceDetails.Service_Name} disabled />
          </label>
          <label>
            Service Type:
            <select value={serviceDetails.Service_Type_ID} onChange={handleServiceTypeChange}>
              {serviceTypes.map((type) => (
                <option key={type.Service_Type_ID} value={type.Service_Type_ID}>{type.Service_Type}</option>
              ))}
            </select>
          </label>
          <label>
            Price:
            <input type="number" value={serviceDetails.Price} onChange={handlePriceChange} />
          </label>
          <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
};

export default ModifyService;
