// ModifyService.js
import React from 'react';
import { useNavigate, useParams, useLocation } from 'react-router-dom';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = location.state || {};
    const { serviceId } = useParams(); // Retrieve the serviceId from the URL

  // Redirect to login if there is no logged in user
  useEffect(() => {
    // Redirect to login if there is no logged in user
    if (!user) {
      navigate('/login');
    }
  }, [user, navigate]);

  return (
    <div className="modify-service-page">
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        <p>Service ID: {serviceId}</p>
        <p>User ID: { user.id }</p>
        {/* Additional fields and form to modify service details */}
      </div>
    </div>
  );
};

export default ModifyService;
