import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
  const { ServiceCatalog_Id } = useParams();
  const location = useLocation();
  const { user } = location.state || {};
  const [serviceDetails, setServiceDetails] = useState({});
  const [serviceTypes, setServiceTypes] = useState([]);
  const [selectedServiceType, setSelectedServiceType] = useState('');

  useEffect(() => {
    const fetchServiceDetailsAndTypes = async () => {
      try {
        const detailsResponse = await axios.get(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`);
        if (detailsResponse.data.success) {
          setServiceDetails(detailsResponse.data.data);
          setSelectedServiceType(detailsResponse.data.data.Service_Type_ID);
          const serviceId = detailsResponse.data.data.Service_Id;

          // Now we make the second API call after the first one has completed
          const typesResponse = await axios.get(`http://localhost:5000/api/servicetypes/${serviceId}`);
          setServiceTypes(typesResponse.data);

          console.log('Service Types Response:', typesResponse.data);
        }
      } catch (error) {
        console.error('Error fetching service details or types:', error);
      }
    };

    if (ServiceCatalog_Id) {
      fetchServiceDetailsAndTypes();
    }
  }, [ServiceCatalog_Id]);

  const handleServiceTypeChange = (e) => {
    setSelectedServiceType(e.target.value);
  };

  const handlePriceChange = (e) => {
    setServiceDetails(prevDetails => ({ ...prevDetails, Price: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updateResponse = await axios.put(`http://localhost:5000/api/servicecatalog/update/${ServiceCatalog_Id}`, {
        ...serviceDetails,
        Service_Type_ID: selectedServiceType,
      });

      if (updateResponse.data.success) {
        alert('Service updated successfully!');
      } else {
        throw new Error(updateResponse.data.message);
      }
    } catch (error) {
      console.error('Error updating service:', error);
      alert('Error updating service: ' + error.message);
    }
  };

  if (!serviceDetails.Service_Name) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Service Name:
            <input type="text" value={serviceDetails.Service_Name || ''} disabled />
          </label>
          <br />
          <label>
            Service Type:
            <select value={selectedServiceType || ''} onChange={handleServiceTypeChange}>
              {serviceTypes.length > 0 ? (
                serviceTypes.map((type) => (
                  <option key={type.Service_Type_ID} value={type.Service_Type_ID}>
                    {type.Service_Type}
                  </option>
                ))
              ) : (
                <option value="">No service types available</option>
              )}
            </select>
          </label>
          <br />
          <label>
            Price:
            <input type="number" value={serviceDetails.Price || ''} onChange={handlePriceChange} />
          </label>
          <br />
          <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
};

export default ModifyService;
