import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import './ModifyService.css';

const ModifyService = () => {
  const { ServiceCatalog_Id } = useParams();
  const location = useLocation();
  const { user } = location.state || {};
  const [serviceDetails, setServiceDetails] = useState(null);
  const [serviceTypes, setServiceTypes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchServiceDetails = async () => {
      try {
        const detailsResponse = await axios.get(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`);
        if (detailsResponse.data.success) {
          setServiceDetails(detailsResponse.data.data);
          // Fetch service types using the correct Service_Id
          const typesResponse = await axios.get(`http://localhost:5000/api/servicetypes/${detailsResponse.data.data.Service_Id}`);
          setServiceTypes(typesResponse.data);
        }
      } catch (error) {
        console.error('Error fetching service details:', error);
      }
      setIsLoading(false);
    };

    fetchServiceDetails();
  }, [ServiceCatalog_Id]);

  const handleServiceTypeChange = (e) => {
    setServiceDetails({ ...serviceDetails, Service_Type_ID: e.target.value });
  };

  const handlePriceChange = (e) => {
    setServiceDetails({ ...serviceDetails, Price: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const updateResponse = await axios.put(`http://localhost:5000/api/servicecatalog/${ServiceCatalog_Id}`, {
        ...serviceDetails,
      });

      if (updateResponse.data.success) {
        alert('Service updated successfully!');
      } else {
        throw new Error(updateResponse.data.message);
      }
    } catch (error) {
      console.error('Error updating service:', error);
      alert('Error updating service: ' + error.message);
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="modify-service-content">
        <h1>Modify Service</h1>
        <form onSubmit={handleSubmit}>
          <label>
            Service Name:
            <input type="text" value={serviceDetails?.Service_Name || ''} disabled />
          </label>
          <br/>
          <label>
            Service Type:
            <select value={serviceDetails?.Service_Type_ID || ''} onChange={handleServiceTypeChange}>
              {serviceTypes.map((type) => (
                <option key={type.Service_Type_ID} value={type.Service_Type_ID}>
                  {type.Service_Type}
                </option>
              ))}
            </select>
          </label>
          <br/>
          <label>
            Price:
            <input type="number" value={serviceDetails?.Price || ''} onChange={handlePriceChange} />
          </label>
          <br/>
          <button type="submit">Save Changes</button>
        </form>
      </div>
    </div>
  );
};

export default ModifyService;
