import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from './AuthContext'; // Assuming you have an AuthContext
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';

const AddService = () => {
  const [services, setServices] = useState([]);
  const [serviceTypes, setServiceTypes] = useState([]);
  const [selectedService, setSelectedService] = useState('');
  const [selectedServiceType, setSelectedServiceType] = useState('');
  const [price, setPrice] = useState('');
  const [description, setDescription] = useState('');
  const { userId } = useContext(AuthContext); // Assuming you store logged in user's id in AuthContext

  useEffect(() => {
    // Fetch initial services list
    axios.get('/api/services').then(response => {
      setServices(response.data);
    });
  }, []);

  useEffect(() => {
    // Fetch service types based on selected service
    if (selectedService) {
      axios.get(`/api/servicetypes/${selectedService}`).then(response => {
        setServiceTypes(response.data);
      });
    }
  }, [selectedService]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/servicecatalog', {
        Service_Id: selectedService,
        Service_Type_ID: selectedServiceType,
        Service_Provider_ID: userId,
        Price: price,
        Description: description
      });
      alert('Service added successfully!');
      // Reset form or redirect as needed
    } catch (error) {
      console.error('Error adding service:', error);
      alert('Failed to add service');
    }
  };

  return (
    <div>
    <SpNavbar user={user} />
    <SpSideNavbar user={user} />
    <form onSubmit={handleSubmit} className="add-service-form">
      <select value={selectedService} onChange={e => setSelectedService(e.target.value)}>
        <option value="">Select Service</option>
        {services.map(service => (
          <option key={service.Service_Id} value={service.Service_Id}>{service.Service_Name}</option>
        ))}
      </select>

      <select value={selectedServiceType} onChange={e => setSelectedServiceType(e.target.value)} disabled={!selectedService}>
        <option value="">Select Service Type</option>
        {serviceTypes.map(type => (
          <option key={type.Service_Type_ID} value={type.Service_Type_ID}>{type.Service_Type}</option>
        ))}
      </select>

      <input type="text" value={price} onChange={e => setPrice(e.target.value)} placeholder="Price" />
      <textarea value={description} onChange={e => setDescription(e.target.value)} placeholder="Description"></textarea>
      <button type="submit">Add Service</button>
    </form>
    </div>
  );
};

export default AddService;
