import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SpNavbar from '../SpNavbar/SpNavbar';
import SpSideNavbar from '../SpSideNav/SPSideNavbar';
import { useLocation } from 'react-router-dom';
import './ListService.css';

const ListService = () => {
  const [services, setServices] = useState([]);
  const [allServiceTypes, setAllServiceTypes] = useState([]);
  const location = useLocation();
  const { user } = location.state || {};

  useEffect(() => {
    const fetchServicesAndTypes = async () => {
      try {
        if (user && user.id) {
          const servicesResponse = await axios.get(`http://localhost:5000/api/servicecatalog/user/${user.id}`);
          setServices(servicesResponse.data.map(service => ({
            ...service,
            isEditing: false,
          })));

          // Assuming this endpoint returns all service types
          console.log('Service_name:', service.Service_Name);
          const serviceTypesResponse = await axios.get('http://localhost:5000/api/servicetypes/${service.Service_Name}');
          setAllServiceTypes(serviceTypesResponse.data);
        }
      } catch (error) {
        console.error('Failed to fetch data:', error);
      }
    };

    fetchServicesAndTypes();
  }, [user.id]);

  const handleEdit = (serviceCatalogId) => {
    setServices(services.map(service => ({
      ...service,
      isEditing: service.ServiceCatalog_Id === serviceCatalogId,
    })));
  };

  const handleChange = (serviceCatalogId, field, value) => {
    setServices(services.map(service => {
      if (service.ServiceCatalog_Id === serviceCatalogId) {
        return { ...service, [field]: value };
      }
      return service;
    }));
  };

  const handleSave = async (serviceCatalogId) => {
    const serviceToUpdate = services.find(service => service.ServiceCatalog_Id === serviceCatalogId);
    try {
      await axios.put(`http://localhost:5000/api/servicecatalog/${serviceCatalogId}`, {
        Service_Id: serviceToUpdate.Service_Id,
        Service_Type_ID: serviceToUpdate.Service_Type_ID,
        Price: serviceToUpdate.Price,
        Description: serviceToUpdate.Description,
        Service_Provider_ID: user.id,
      });
      handleEdit(serviceCatalogId); // Toggle editing off
    } catch (error) {
      console.error('Failed to update service:', error);
    }
  };

  const handleDelete = async (serviceCatalogId) => {
    try {
      await axios.delete(`http://localhost:5000/api/servicecatalog/${serviceCatalogId}`);
      setServices(services.filter(service => service.ServiceCatalog_Id !== serviceCatalogId));
    } catch (error) {
      console.error('Failed to delete service:', error);
    }
  };

  if (!user) {
    return <div><p>User details not available.</p></div>;
  }

  return (
    <div>
      <SpNavbar user={user} />
      <SpSideNavbar user={user} />
      <div className="service-list-container">
        <table>
          <thead>
            <tr>
              <th>Service Name</th>
              <th>Service Type</th>
              <th>Price</th>
              <th>Description</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {services.map(service => (
              <tr key={service.ServiceCatalog_Id}>
                <td>{service.Service_Name}</td>
                <td>
                  {service.isEditing ? (
                    <select
                      value={service.Service_Type_ID}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Service_Type_ID', e.target.value)}
                    >
                      {allServiceTypes.map(type => (
                        <option key={type.Service_Type_ID} value={type.Service_Type_ID}>{type.Service_Type}</option>
                      ))}
                    </select>
                  ) : (
                    service.Service_Type
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <input
                      type="number"
                      value={service.Price}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Price', e.target.value)}
                    />
                  ) : (
                    service.Price
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <input
                      type="text"
                      value={service.Description}
                      onChange={(e) => handleChange(service.ServiceCatalog_Id, 'Description', e.target.value)}
                    />
                  ) : (
                    service.Description
                  )}
                </td>
                <td>
                  {service.isEditing ? (
                    <>
                      <button onClick={() => handleSave(service.ServiceCatalog_Id)}>Save</button>
                      <button onClick={() => handleEdit(service.ServiceCatalog_Id)}>Cancel</button>
                    </>
                  ) : (
                    <button onClick={() => handleEdit(service.ServiceCatalog_Id)}>Modify</button>
                  )}
                  <button onClick={() => handleDelete(service.ServiceCatalog_Id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListService;
